import React, { useState } from "react";

const D = () => {
  const [formData, setFormData] = useState({
    latitude: "",
    longitude: "",
    Neighbourhood: "",
    occurrencehour: "",
    occurrencedayofweek: "",
    premisetype: "",
  });

  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    try {
      const response = await fetch("http://127.0.0.1:8000/api/predict-crime/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      if (response.ok) {
        setPrediction(data);
      } else {
        setError(data.error || "Prediction failed. Please check your input values.");
      }
    } catch (err) {
      setError("Server error. Please try again later.");
    }
  };

  return (
    <div>
      <h2>Crime Prediction</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="latitude" placeholder="Latitude" onChange={handleChange} required />
        <input type="text" name="longitude" placeholder="Longitude" onChange={handleChange} required />
        <input type="text" name="Neighbourhood" placeholder="Neighbourhood" onChange={handleChange} required />
        <input type="number" name="occurrencehour" placeholder="Occurrence Hour" onChange={handleChange} required />
        <input type="text" name="occurrencedayofweek" placeholder="Day of Week" onChange={handleChange} required />
        <input type="text" name="premisetype" placeholder="Premise Type" onChange={handleChange} required />
        <button type="submit">Predict Crime</button>
      </form>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {prediction && (
        <div>
          <h3>Prediction Result</h3>
          <p><strong>Crime Type:</strong> {prediction.crime_type}</p>
          <p><strong>Crime Rate Percentage:</strong> {prediction.crime_rate_percentage}%</p>
          <p><strong>Risk Level:</strong> {prediction.risk_level}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
