import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, CircleMarker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import "./RiskMapPage.css"; // Importing custom CSS for styling

const getRiskColor = (riskLevel) => {
  if (riskLevel === "High") return "#FF4C4C";
  if (riskLevel === "Medium") return "#FFA500";
  return "#00C851"; // Low risk (Green)
};

const RiskMapPage = () => {
  const [crimeData, setCrimeData] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/all-crime-data/")
      .then((res) => res.json())
      .then((data) => setCrimeData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div className="risk-map-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Crime Risk Analysis</h2>
        <p>Live data visualization of crime risk levels.</p>
      </div>

      {/* Main Map Section */}
      <div className="map-section">
        <h2 className="map-title">Risk Map Visualization</h2>
        <MapContainer center={[43.6569, -79.4052]} zoom={13} className="map-style">
          {/* Light-themed map for professional look */}
          <TileLayer url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png" />
          
          {crimeData.map((crime, index) => (
            crime.latitude && crime.longitude ? (
              <CircleMarker 
                key={index}
                center={[crime.latitude, crime.longitude]}
                radius={8}
                color={getRiskColor(crime.risk_level)}
                fillColor={getRiskColor(crime.risk_level)}
                fillOpacity={0.6}>
                <Popup>
                  <strong>Location:</strong> {crime.Neighbourhood} <br />
                  <strong>Risk Level:</strong> {crime.risk_level} <br />
                  <strong>Crime Rate:</strong> {crime.crime_rate_percentage}%
                </Popup>
              </CircleMarker>
            ) : null
          ))}
        </MapContainer>
      </div>
    </div>
  );
};

export default RiskMapPage;
