import React, { useState } from "react";
import axios from "axios";
import RiskMap from "./RiskMap"; // Import RiskMap component

const Dashboard = () => {
  const [predictions, setPredictions] = useState([]); // Store multiple predictions
  const [error, setError] = useState("");
  const [inputData, setInputData] = useState({
    latitude: "",
    longitude: "",
    Neighbourhood: "",
    occurrencehour: "",
    occurrencedayofweek: "",
    premisetype: "",
  });

  const handleChange = (e) => {
    setInputData({ ...inputData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://127.0.0.1:8000/api/predict-crime/", inputData)
      .then((response) => {
        console.log("Prediction Response:", response.data); // Debugging
        setPredictions([...predictions, response.data]); // Store new predictions
        setError(""); // Reset error on success
      })
      .catch((error) => {
        console.error("Error Predicting Crime:", error.response ? error.response.data : error.message);
        setError("Prediction failed. Please check your input values.");
      });
  };

  return (
    <div>
      <h1>Crime Prediction Dashboard</h1>

      {/* Crime Prediction Form */}
      <h2>Predict Crime Type & Risk Level</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="latitude"
          placeholder="Latitude"
          value={inputData.latitude}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="longitude"
          placeholder="Longitude"
          value={inputData.longitude}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="Neighbourhood"
          placeholder="Neighbourhood"
          value={inputData.Neighbourhood}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="occurrencehour"
          placeholder="Hour (0-23)"
          value={inputData.occurrencehour}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="occurrencedayofweek"
          placeholder="Day of the week"
          value={inputData.occurrencedayofweek}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="premisetype"
          placeholder="Premise Type"
          value={inputData.premisetype}
          onChange={handleChange}
          required
        />
        <button type="submit">Predict Crime</button>
      </form>

      {/* Show Error if Prediction Fails */}
      {error && <h3 style={{ color: "red" }}>{error}</h3>}

      {/* Prediction Result */}
      {predictions.length > 0 && (
        <div>
          <h2>Latest Prediction</h2>
          <h3>Predicted Crime Type: {String(predictions[predictions.length - 1].crime_type)}</h3>
          <h3>Estimated Crime Rate: {String(predictions[predictions.length - 1].crime_rate_percentage)}%</h3>
          <h3>
            Risk Level:{" "}
            <span
              style={{
                color:
                  predictions[predictions.length - 1].risk_level === "High"
                    ? "red"
                    : predictions[predictions.length - 1].risk_level === "Medium"
                    ? "orange"
                    : "green",
              }}
            >
              {String(predictions[predictions.length - 1].risk_level)}
            </span>
          </h3>
        </div>
      )}

      {/* Display Risk Level Map */}
      {predictions.length > 0 && <RiskMap predictions={predictions} />}
    </div>
  );
};

export default Dashboard;
