import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, CircleMarker, Popup } from "react-leaflet";
import "./RiskMapPage.css"; // Import the CSS file for styling

const getRiskColor = (riskLevel) => {
  if (riskLevel === "High") return "red";
  if (riskLevel === "Medium") return "orange";
  return "green"; // Low risk
};

const RiskMapPage = () => {
  const [crimeData, setCrimeData] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/all-crime-data/")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched Crime Data:", data);
        setCrimeData(data);
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div className="risk-map-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Women Safety Analytics</h2>
        <ul>
          <li>Dashboard</li>
          <li>Crime Predictions</li>
          <li>Risk Map</li>
          <li>Reports</li>
        </ul>
      </div>

      {/* Map Section */}
      <div className="map-section">
        <h2 className="map-title">Crime Risk Map</h2>
        <MapContainer center={[43.6569, -79.4052]} zoom={12} className="map-style">
          <TileLayer url="https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png" />
          {crimeData.map((crime, index) =>
            crime.latitude && crime.longitude ? (
              <CircleMarker
                key={index}
                center={[crime.latitude, crime.longitude]}
                radius={10}
                color={getRiskColor(crime.risk_level)}
                fillColor={getRiskColor(crime.risk_level)}
                fillOpacity={0.6}
              >
                <Popup>
                  <strong>Location:</strong> {crime.Neighbourhood} <br />
                  <strong>Risk Level:</strong> {crime.risk_level} <br />
                  <strong>Crime Rate:</strong> {crime.crime_rate_percentage}%
                </Popup>
              </CircleMarker>
            ) : null
          )}
        </MapContainer>
      </div>
    </div>
  );
};

export default RiskMapPage;
