import React, { useState } from "react";

const CrimePrediction = () => {
  const [predictionResult, setPredictionResult] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    setLoading(true);
    setError(null);
    setPredictionResult(null);

    try {
      const response = await fetch("http://127.0.0.1:8000/api/predict-crime/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          latitude: 43.6568824,
          longitude: -79.4052277,
          neighbourhood: "University (79)",
          occurrence_hour: 3,
          occurrence_day_of_week: "Friday",
          premise_type: "Commercial",
        }),
      });

      const data = await response.json();

      if (response.ok) {
        setPredictionResult(data);
      } else {
        setError(data.error || "Something went wrong.");
      }
    } catch (err) {
      setError("Network error. Please try again.");
    }

    setLoading(false);
  };

  return (
    <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
      <h2>Crime Prediction</h2>
      <button onClick={handlePredict} disabled={loading}>
        {loading ? "Predicting..." : "Predict Crime"}
      </button>

      {error && <p style={{ color: "red", marginTop: "10px" }}>{error}</p>}

      {predictionResult && (
        <div style={{ marginTop: "20px", padding: "10px", border: "1px solid #ccc", borderRadius: "8px" }}>
          <h3>Prediction Result:</h3>
          <p><strong>Crime Type:</strong> {predictionResult.crime_type}</p>
          <p><strong>Crime Rate:</strong> {predictionResult.crime_rate_percentage}%</p>
          <p><strong>Risk Level:</strong> {predictionResult.risk_level}</p>
        </div>
      )}
    </div>
  );
};

export default CrimePrediction;
