import React, { useEffect, useState } from "react";
import { 
  BarChart, Bar, PieChart, Pie, LineChart, Line, 
  XAxis, YAxis, Tooltip, Legend, CartesianGrid, 
  ResponsiveContainer 
} from "recharts";

const CrimeDashboard = () => {
  const [crimeData, setCrimeData] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/all-crime-data/")
      .then((res) => res.json())
      .then((data) => setCrimeData(data))
      .catch((error) => console.error("Error fetching crime data:", error));
  }, []);

  // Prepare data for visualizations
  const neighborhoodCrimeCount = Object.values(
    crimeData.reduce((acc, crime) => {
      acc[crime.Neighbourhood] = acc[crime.Neighbourhood] || { name: crime.Neighbourhood, count: 0 };
      acc[crime.Neighbourhood].count += 1;
      return acc;
    }, {})
  );

  const crimeByHour = Object.values(
    crimeData.reduce((acc, crime) => {
      acc[crime.occurrencehour] = acc[crime.occurrencehour] || { hour: crime.occurrencehour, count: 0 };
      acc[crime.occurrencehour].count += 1;
      return acc;
    }, {})
  ).sort((a, b) => a.hour - b.hour);

  const crimeByPremises = Object.values(
    crimeData.reduce((acc, crime) => {
      acc[crime.premisetype] = acc[crime.premisetype] || { name: crime.premisetype, value: 0 };
      acc[crime.premisetype].value += 1;
      return acc;
    }, {})
  );

  const riskLevelDistribution = Object.values(
    crimeData.reduce((acc, crime) => {
      acc[crime.risk_level] = acc[crime.risk_level] || { name: crime.risk_level, value: 0 };
      acc[crime.risk_level].value += 1;
      return acc;
    }, {})
  );

  return (
    <div style={styles.pageContainer}>
      <h2 style={styles.header}>Crime Dashboard</h2>

      {/* Crime Count by Neighborhood */}
      <div style={styles.chartContainer}>
        <h3 style={styles.chartTitle}>Crime Count by Neighborhood</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={neighborhoodCrimeCount}>
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="count" fill="#007bff" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* Crime by Hour of the Day */}
      <div style={styles.chartContainer}>
        <h3 style={styles.chartTitle}>Crime Occurrence by Hour</h3>
        <ResponsiveContainer width="100%" height={300}>
          <LineChart data={crimeByHour}>
            <XAxis dataKey="hour" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line type="monotone" dataKey="count" stroke="#28a745" />
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* Crime Distribution by Premises Type */}
      <div style={styles.chartContainer}>
        <h3 style={styles.chartTitle}>Crime Distribution by Premises Type</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie data={crimeByPremises} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} fill="#ffc107" label />
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
      </div>

      {/* Risk Level Distribution */}
      <div style={styles.chartContainer}>
        <h3 style={styles.chartTitle}>Risk Level Distribution</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie data={riskLevelDistribution} dataKey="value" nameKey="name" cx="50%" cy="50%" outerRadius={100} fill="#dc3545" label />
            <Tooltip />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

// Styles for the dashboard
const styles = {
  pageContainer: {
    backgroundColor: "#F5FFFA", // MintCream background
    padding: "20px",
    minHeight: "100vh"
  },
  header: {
    textAlign: "center",
    color: "#343a40",
    fontSize: "24px",
    marginBottom: "20px"
  },
  chartContainer: {
    backgroundColor: "#ffffff", // White cards
    padding: "20px",
    marginBottom: "30px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)"
  },
  chartTitle: {
    color: "#495057",
    fontSize: "18px",
    marginBottom: "10px"
  }
};

export default CrimeDashboard;
