import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import { Chart as ChartJS, BarElement, CategoryScale, LinearScale, ArcElement, Tooltip, Legend } from "chart.js";

ChartJS.register(BarElement, CategoryScale, LinearScale, ArcElement, Tooltip, Legend);

const Dashboard = () => {
  const [crimeData, setCrimeData] = useState([]);
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState("");
  const [inputData, setInputData] = useState({
    latitude: "",
    longitude: "",
    Neighbourhood: "",
    occurrence_hour: "",
    occurrenceday_of_week: "",
    premise_type: "",
  });

  useEffect(() => {
    axios
      .post("http://127.0.0.1:8000/api/crime-data/") // Ensure correct backend route
      .then((response) => {
        console.log("Crime Data Response:", response.data);  // Debugging
        setCrimeData(response.data);
      })
      .catch((error) => {
        console.error("Error Fetching Crime Data:", error.response ? error.response.data : error.message);
        setError("Error fetching crime data. Please try again later.");
      });
  }, []);

  const handleChange = (e) => {
    setInputData({ ...inputData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://127.0.0.1:8000/api/predict-crime/", inputData) // Ensure correct backend route
      .then((response) => {
        console.log("Prediction Response:", response.data);  // Debugging
        setPrediction(response.data);
      })
      .catch((error) => {
        console.error("Error Predicting Crime:", error.response ? error.response.data : error.message);
        setError("Prediction failed. Please check your input values.");
      });
  };



  // Aggregate crime type counts for visualization
  const crimeTypeCounts = {};
  crimeData.forEach((crime) => {
    crimeTypeCounts[crime.crime_type] =
      (crimeTypeCounts[crime.crime_type] || 0) + 1;
  });

  // Chart data for bar and pie chart
  const colors = ["#FF6384", "#36A2EB", "#FFCE56", "#8A2BE2", "#4BC0C0", "#9966FF"];
  const chartData = {
    labels: Object.keys(crimeTypeCounts),
    datasets: [
      {
        label: "Crime Frequency",
        data: Object.values(crimeTypeCounts),
        backgroundColor: colors.slice(0, Object.keys(crimeTypeCounts).length),
      },
    ],
  };

  if (error) {
    return <h2 style={{ color: "red" }}>{error}</h2>;
  }

  return (
    <div>
      <h1>Crime Analytics Dashboard</h1>

      {/* Crime Data Table */}
      <table border="1">
        <thead>
          <tr>
            <th>Neighbourhood</th>
            <th>Crime Type</th>
            <th>Time</th>
            <th>Premise</th>
          </tr>
        </thead>
        <tbody>
          {crimeData.map((crime, index) => (
            <tr key={index}>
              <td>{crime.neighbourhood}</td>
              <td>{crime.crime_type}</td>
              <td>
                {crime.occurrence_hour}:00 on {crime.occurrence_day_of_week}
              </td>
              <td>{crime.premise_type}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Graphs */}
      {crimeData.length > 0 && (
        <>
          <div style={{ width: "500px", marginTop: "20px" }}>
            <h3>Crime Distribution</h3>
            <Bar data={chartData} />
          </div>

          <div style={{ width: "400px", marginTop: "20px" }}>
            <h3>Crime Type Pie Chart</h3>
            <Pie data={chartData} />
          </div>
        </>
      )}

      {/* Crime Prediction Form */}
      <h2>Predict Crime Type & Risk Level</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="latitude"
          placeholder="Latitude"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="longitude"
          placeholder="Longitude"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="neighbourhood"
          placeholder="Neighbourhood"
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="occurrence_hour"
          placeholder="Hour (0-23)"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="occurrence_day_of_week"
          placeholder="Day of the week"
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="premise_type"
          placeholder="Premise Type"
          onChange={handleChange}
          required
        />
        <button type="submit">Predict Crime</button>
      </form>

      {/* Prediction Result */}
      {prediction && (
        <div>
          <h3>Predicted Crime Type: {prediction.crime_type}</h3>
          <h3>Estimated Crime Rate: {prediction.crime_rate_percentage}%</h3>
          <h3>
            Risk Level:{" "}
            <span
              style={{
                color:
                  prediction.risk_level === "High"
                    ? "red"
                    : prediction.risk_level === "Medium"
                    ? "orange"
                    : "green",
              }}
            >
              {prediction.risk_level}
            </span>
          </h3>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
