import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Fix default Leaflet marker icon issue
import markerIconPng from "leaflet/dist/images/marker-icon.png";
import markerShadowPng from "leaflet/dist/images/marker-shadow.png";

const RiskMapPage = () => {
    const [crimeData, setCrimeData] = useState([]);

    // Fetch crime data from Django API
    useEffect(() => {
        fetch("http://127.0.0.1:8000/api/all-crime-data/")
            .then((response) => response.json())
            .then((data) => {
                console.log("Crime Data:", data); // Debugging output
                setCrimeData(data);
            })
            .catch((error) => console.error("Error fetching crime data:", error));
    }, []);

    // Custom marker icons based on risk level
    const getMarkerIcon = (riskLevel) => {
        let color;
        if (riskLevel === "High") color = "red";
        else if (riskLevel === "Medium") color = "orange";
        else color = "green";

        return new L.Icon({
            iconUrl: markerIconPng,
            shadowUrl: markerShadowPng,
            iconSize: [25, 41],
            iconAnchor: [12, 41],
            popupAnchor: [1, -34],
            shadowSize: [41, 41],
            className: `leaflet-marker-${color}`, // Optional custom styling
        });
    };

    return (
        <div>
            <h2>Crime Risk Level Map</h2>
            <MapContainer center={[43.7, -79.4]} zoom={10} style={{ height: "500px", width: "100%" }}>
                <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />

                {crimeData.length > 0 && (
                    <MarkerClusterGroup>
                        {crimeData.map((crime, index) => (
                            <Marker
                                key={index}
                                position={[parseFloat(crime.latitude), parseFloat(crime.longitude)]}
                                icon={getMarkerIcon(crime.risk_level)}
                            >
                                <Popup>
                                    <strong>Crime Type:</strong> {crime.crime_type} <br />
                                    <strong>Risk Level:</strong> {crime.risk_level} <br />
                                    <strong>Crime Rate:</strong> {crime.crime_rate_percentage}%
                                </Popup>
                            </Marker>
                        ))}
                    </MarkerClusterGroup>
                )}
            </MapContainer>
        </div>
    );
};

export default RiskMapPage;
