import React, { useState } from "react";
import axios from "axios";

const Dashboard = () => {
  const [prediction, setPrediction] = useState(null);
  const [error, setError] = useState("");
  const [inputData, setInputData] = useState({
    latitude: "",
    longitude: "",
    Neighbourhood: "",
    occurrencehour: "",
    occurrencedayofweek: "",
    premisetype: "",
  });

  const handleChange = (e) => {
    setInputData({ ...inputData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/predict-crime/",
        inputData,
        {
          headers: { "Content-Type": "application/json" },
        }
      );
      console.log("Prediction Response:", response.data); // Debugging
      setPrediction(response.data);
      setError(""); // Clear previous errors
    } catch (error) {
      console.error(
        "Error Predicting Crime:",
        error.response ? error.response.data : error.message
      );
      setError("Prediction failed. Please check your input values.");
    }
  };

  return (
    <div>
      <h1>Crime Prediction Dashboard</h1>

      {/* Crime Prediction Form */}
      <h2>Predict Crime Type & Risk Level</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="latitude"
          placeholder="Latitude"
          value={inputData.latitude}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="longitude"
          placeholder="Longitude"
          value={inputData.longitude}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="neighbourhood"
          placeholder="Neighbourhood"
          value={inputData.neighbourhood}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="occurrence_hour"
          placeholder="Hour (0-23)"
          value={inputData.occurrence_hour}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="occurrence_day_of_week"
          placeholder="Day of the week"
          value={inputData.occurrence_day_of_week}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="premise_type"
          placeholder="Premise Type"
          value={inputData.premise_type}
          onChange={handleChange}
          required
        />
        <button type="submit">Predict Crime</button>
      </form>

      {/* Show Error if Prediction Fails */}
      {error && <h3 style={{ color: "red" }}>{error}</h3>}

      {/* Prediction Result */}
      {prediction && (
        <div>
          <h3>Predicted Crime Type: {prediction.crime_type}</h3>
          <h3>Estimated Crime Rate: {prediction.crime_rate_percentage}%</h3>
          <h3>
            Risk Level:{" "}
            <span
              style={{
                color:
                  prediction.risk_level === "High"
                    ? "red"
                    : prediction.risk_level === "Medium"
                    ? "orange"
                    : "green",
              }}
            >
              {prediction.risk_level}
            </span>
          </h3>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
